// This code processes a JSON string within XML tags and outputs individual items
function processItems($input) {
    // Get the input text
    const inputText = $input.first().json.output;

    // Extract JSON string from between XML tags
    const jsonMatch = inputText.match(/<json_output>\n([\s\S]*?)\n<\/json_output>/);

    if (!jsonMatch) {
        throw new Error('No valid JSON found between xml tags');
    }

    let jsonString = jsonMatch[1];

    // Parse the JSON
    let items;
    try {
        items = JSON.parse(jsonString);
    } catch (error) {
        throw new Error(`Failed to parse JSON: ${error.message}`);
    }

    // Validate that we have an array
    if (!Array.isArray(items)) {
        throw new Error('Parsed JSON is not an array');
    }

    // Transform each item into the output format
    // This preserves all fields dynamically
    const outputs = items.map(item => ({
        json: {
            ...item  // Spread operator to include all fields from the original item
        }
    }));

    return outputs;
}

// Process the items and return the result
return processItems($input);

