function processHooks($input) {
    // Get the input text
    const inputText = $input.first().json.text;

    // Extract JSON string from between XML tags
    const jsonMatch = inputText.match(/<json_output>\s*([\s\S]*?)\s*<\/json_output>/);

    if (!jsonMatch) {
        throw new Error('No JSON found between json_output tags');
    }

    // Parse the JSON string
    let items;
    try {
        items = JSON.parse(jsonMatch[1].trim());
    } catch (error) {
        console.log('JSON parsing failed:', error.message);
        throw new Error(`Invalid JSON format: ${error.message}`);
    }

    // Validate the structure
    if (!Array.isArray(items)) {
        throw new Error('Parsed JSON is not an array');
    }

    // Transform each item
    const outputs = items.map((item, index) => {
        if (!item.hooks) {
            console.log(`Warning: Item ${index + 1} missing hooks property`);
            return null;
        }

        // Split hooks on newlines and filter empty lines
        const hookOptions = item.hooks
            .split('\\n')
            .filter(hook => hook.trim() !== '');

        // Join with double newlines for readability
        const hookString = hookOptions.join('\n\n');

        return {
            json: {
                hooks: hookString,
                originalIndex: index + 1
            }
        };
    }).filter(item => item !== null);

    // Validate output
    if (outputs.length === 0) {
        throw new Error('No valid items were processed');
    }

    return outputs;
}

// Execute the function
