try {
    if (!response?.data?.transcripts) {
        throw new Error('Invalid data structure: missing transcripts');
    }

    const transcripts = response.data.transcripts;
    let combinedText = '';
    let transcriptSource = null;

    // Priority order for transcripts
    const priorityOrder = [
        { lang: 'en', type: 'custom' },
        { lang: 'en_auto', type: 'custom' },
        { lang: 'en_auto_auto', type: 'custom' }
    ];

    // Find first available transcript following priority order
    for (const priority of priorityOrder) {
        const langTranscripts = transcripts[priority.lang];
        if (langTranscripts?.[priority.type] && Array.isArray(langTranscripts[priority.type])) {
            const transcript = langTranscripts[priority.type];
            const texts = transcript.map(entry => entry.text);
            combinedText = texts.join(' ').trim();
            transcriptSource = `${priority.lang}.${priority.type}`;
            break; // Stop after finding first valid transcript
        }
    }

    // If no prioritized transcript found, throw error
    if (!combinedText) {
        throw new Error('No valid transcript found in any of the priority sources');
    }

    // Clean up the text (remove multiple spaces)
    combinedText = combinedText.replace(/\s+/g, ' ');

    return {
        json: {
            combinedText,
            totalLength: combinedText.length,
            transcriptSource, // Shows which transcript was used
            videoId: response.data.videoId,
            success: response.code === 100000
        }
    };

} catch (error) {
    return {
        json: {
            error: error.message,
            combinedText: '',
            totalLength: 0,
            transcriptSource: null,
            videoId: null,
            success: false
        }
    };
}
