options.customCss:
:root {
  /* Colors */
  --chat--color-primary: #4fb9d6; /* Send button background */
  --chat--color-white: #ffffff;
  --chat--color-light: #0b0f1a;
  --chat--color-light-shade-50: #151c27;
  --chat--color-light-shade-100: #1e2733;
  --chat--color-dark: #09101c;
  --chat--color-typing: #9a9d9e;

  /* Fonts */
  --chat--font-family: 'Open Sans', sans-serif;
  --chat--heading--font-family: 'Nunito', sans-serif;

  /* Layout & Dimensions */
  --chat--spacing: 0.8rem;
  --chat--border-radius: 1.2rem;
  --chat--window--width: 100vw;
  --chat--window--height: 100vh;
  --chat--window--border: none;

  /* Header */
  --chat--header-height: auto;
  --chat--header--padding: 0.5rem 1rem;
  --chat--header--background: #09101b;
  --chat--header--color: var(--chat--color-white);
  --chat--heading--font-size: 1.5rem;
  --chat--subtitle--font-size: 0.85rem;
  --chat--header--heading--margin-bottom: 0.5rem; /* or 30px */
  --chat--header--subtitle--margin-top: 0px;

  /* Message bubbles */
  --chat--message--font-size: 0.85rem;
  --chat--message--padding: 1rem;
  --chat--message--border-radius: 1.2rem;
  --chat--message-line-height: 1.6;
  --chat--message--margin-bottom: 1.3rem;
  --chat--message--bot--background: #151c27;
  --chat--message--bot--color: #ffffff;
  --chat--message--user--background: #4fb9d6;
  --chat--message--user--color: #ffffff;

  /* Input */
  --chat--input--background: #151c27;
  --chat--input--text-color: #ffffff;
  --chat--input--placeholder--color: #9a9d9e;
  --chat--textarea--height: 48px;
  --chat--input--border: none;
  --chat--input--padding: 0.9rem;

  /* Send button */
  --chat--input--send--button--background: #4fb9d6;
  --chat--input--send--button--color: #ffffff;
  --chat--input--send--button--background-hover: #3caac6;
  --chat--input--send--button--border-radius: 50px;
  --chat--input--send--button--width: 44px;
  --chat--input--send--button--height: 44px;

  /* Chat background */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-light);
}

/* Bubble style */
.chat-message {
  max-width: 90%;
  font-family: 'Open Sans', sans-serif;
  padding: 1rem 1.2rem !important;
  margin-bottom: 1.3rem !important;
  border-radius: 1.2rem !important;
}

/* Make the icon inside the send button white */
.chat-input__send-button svg {
  fill: #ffffff !important;
}

.chat-powered-by {
  display: none !important;
}

.chat-button {
  transition: 
    color 0.3s ease-in-out,
    background-color 0.3s ease-in-out,
    border-color 0.3s ease-in-out,
    box-shadow 0.3s ease-in-out;
  background-color: var(--chat--color-primary); /* starting color */
  color: #ffffff; /* text color */
}

/* Hover state: white background, matching text color */
.chat-button:hover {
  background-color: #ffffff !important;
  color: var(--chat--color-primary) !important;
  border: 1px solid var(--chat--color-primary);
}

.chat-input-send-button[data-v-2a7fb1c3] svg {
  fill: #ffffff !important;
}
