const htmlContent = $('Loop Over Items').first().json.data[0].body.text; // Replace this with the field containing your HTML content

// Function to strip HTML tags and clean the text
const extractPlainText = (html) => {
    if (!html) return "";

    // Remove script and style tags along with their content
    const withoutScriptsAndStyles = html.replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, "")
                                        .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, "");

    // Remove all remaining HTML tags
    const strippedText = withoutScriptsAndStyles.replace(/<\/?[^>]+(>|$)/g, "");

    // Replace HTML entities with their plain-text equivalents
    const decodedText = strippedText.replace(/&nbsp;/gi, " ")
                                    .replace(/&amp;/gi, "&")
                                    .replace(/&lt;/gi, "<")
                                    .replace(/&gt;/gi, ">")
                                    .replace(/&quot;/gi, "\"")
                                    .replace(/&#39;/gi, "'");

    // Replace multiple spaces, tabs, and newlines with a single space
    const cleanedText = decodedText.replace(/\s+/g, " ").trim();

    return cleanedText;
};

// Call the function to process the HTML content
const filteredText = extractPlainText(htmlContent);

// Return the plain text as the output of this node
return {
    filteredText: filteredText,
};

const inputText = $input.first().json.text;

// 1. Replace actual "\n" with HTML line breaks
const textWithBreaks = inputText.replace(/\n/g, '<br>');

// 2. Add "-Arun" and "Sent from iPhone" with proper spacing
const formattedText = `
  <div style="color: #000000; font-family: Arial, sans-serif;">
    ${textWithBreaks}<br><br>- Arun<br><br>Sent from iPhone
  </div>
`;

// 3. Return the formatted text
return [{ json: { formattedText } }];

